  }
  if (id) {
    matchConditions._id = { $ne: id };
  }
  const blogs = await Blog.find(matchConditions)
    .skip(skip)
    .limit(limit)
    .sort({ createdAt: -1 });
  if (!blogs) {
    return [];
  }
  return blogs;
}

