async function generateId() {
  const chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
  let id = "";
  const bytes = crypto.randomBytes(8);

  for (let i = 0; i < 8; i++) {
    id += chars[bytes[i] % chars.length];
  }

  const idExists = await Investor.findOne({ publicId: id });

  if (idExists) {
    return generateId();
  } else {
    return id;
  }
}

